import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';

var url=`http://localhost:2234/cricketrs`
class restapi extends Component {
    constructor(props) {
        super(props);
        this.state={users:[]}

    }

    HandlingDeleteData=()=>
    {
        if(window.confirm("DoYouWantToDeleteTheIdPermanently"))
        {
            axios.delete(`http://localhost:2234/cricketrs`)
        }
    }

    render() {
        return (
            <>
<div className="p-5">
                 <h2>Axios Rest Api Calls</h2> 
                 
                     <table className="table table-light w-85 mx-auto my-3">
                             <thead>
                                 <tr>
                                 <th>ID</th>
                                 <th>FIRSTNAME</th>
                                 <th>LASTNAME</th>
                                 <th>EMAIL</th>
                                 <th>PHONE</th>
                                 <th>ACTIONS</th>
                                 </tr>
                             </thead>

                         <tbody className="table table-dark">
                             {this.state.users.map( (res, index)=>{
                                 return(<tr key={index}>
                                         <td>{res.id}</td>
                                         <td>{res.first_name}</td>
                                         <td>{res.last_name}</td>
                                         <td>{res.email}</td>
                                         <td>{res.phone}</td>
                                         <td><Link to ={`restapi/${res.id}`} className="btn btn-primary text-primary">View</Link></td>
                                         <td><Link className="btn btn-warning text-warning">Edit</Link></td>
                                         <td><button onClick={this.HandlingDeleteData(res.id)} className="btn btn-danger text-danger">Delete</button></td>
                                     </tr>
                                 )
                             }
                             )
                             }

                             
                         </tbody>
                     </table>
                 
            </div>
         
            </>
        );
    }
    componentDidMount(){axios.get(url)
                             .then((apiresult)=>this.setState({users:apiresult.data}))



    }
}



export default restapi;
