import {connect} from 'react-redux'

import {bindActionCreators} from 'redux'

import Actions from './Action/index.js'



class ReactReduxConnection extends Component {
    constructor(props) {
        super(props);
    }
    componentDidMount()
    {
        this.props.Actions()
    }
    render() {
        return (
            <div>
                {
                    this
